import React, { useEffect, useRef, useState } from "react";
import OtherCurrencies from "./OtherCurrencies";

function Exchange({ data }) {
    const commons = ["USD", "AZN", "TRY", "EUR"];
    const [input, setInput] = useState("");
    const [result, setResult] = useState({});
    const isInitialMount = useRef(true);
  
    if (input < 0) {
      setInput(input * -1);
    }
  
    const [rate, setRate] = useState({
      from: "",
      to: "",
    });
  
    const [error, setError] = useState({
      from: "",
      to: "",
    });
  
    const setValue = (e) => {
      const { name, value } = e.target;
      setRate({ ...rate, [name]: value });
      setError({ ...error, [name]: "" });
    };
  
    useEffect(() => {
      if (isInitialMount.current) {
        isInitialMount.current = false;
      } else {
        exchangeRate();
      }
    }, [input]);
  
    const exchangeRate = () => {
      if (rate.from.length < 1) {
        setError({ ...error, from: "Choose Currency" });
      } else if (rate.to.length === 0) {
        setError({ ...error, to: "Choose Currency" });
      } else {
        fetch(
          `https://v6.exchangerate-api.com/v6/830c6d13ea14f7c7ad325357/latest/${rate.from}`
        )
          .then((res) => res.json())
          .then((e) => {
            let obj = {
              [rate.to]: (e.conversion_rates[rate.to] * input).toFixed(2),
            };
            commons.map(
              (a) => (obj[a] = (e.conversion_rates[a] * input).toFixed(2))
            );
            setResult(obj);
          });
      }
    };



  return (
    <div className="main">
      <h1 className="mt-3">Currency Exchange</h1>

      <div className="select">
        <h5>dan: </h5>
        <select onChange={(e) => setValue(e)} name="from" style={{ cursor: "pointer" }} defaultValue="" >
          <option value="">Valyutani secin</option>


          {
            Object.keys(data).map((index, key) => {
                return (
                    <option key={key} value={index}>{index}</option>
                )
            })
          }
          
        </select>

        {
            error.from.length > 0 ? (
                <span>{error.from}</span>
  ) : ( "")
        }

        <span>
          <i className="fa-solid fa-right-left"></i>
        </span>

        <h5>a: </h5>

        <select onChange={(e) => setValue(e)} name="to" style={{ cursor: "pointer" }} defaultValue="">
          <option value="">Valyutani secin</option>
          <option value="">TR</option>
        </select>
      </div>

      <div className="amount mt-4">
        <h5 className="text-center">Amount</h5>
        <div>
          <input type="number" />
          <button className="changeBtn">
            <i className="fa-solid fa-rotate"></i>
          </button>
        </div>

        <p className="text-center mt-3">netice</p>
      </div>

      <OtherCurrencies />
    </div>
  );
}

export default Exchange;
