import React, { useEffect, useRef, useState } from "react";
import OtherCurrencies from "./OtherCurrencies";

function Exchange({ data }) {
  const commons = ["USD", "AZN", "TRY", "EURO"];
  const [input, setInput] = useState("");

  const [ result, setResult] = useState({})

  const isInitialMount = useRef(true)


  if ( input < 0 ) {
    setInput(input)
  }

  return (
    <div className="main">
      <h1 className="mt-3">Currency Exchange</h1>

      <div className="select">
        <h5>dan: </h5>
        <select name="from" style={{ cursor: "pointer" }} defaultValue="">
          <option value="">Valyutani secin</option>
          <option value="">aZN</option>
          <option value="">DOLLAR</option>
        </select>

        <span>
          <i className="fa-solid fa-right-left"></i>
        </span>

        <h5>a: </h5>

        <select name="to" style={{ cursor: "pointer" }} defaultValue="">
          <option value="">Valyutani secin</option>
          <option value="">TR</option>
        </select>
      </div>

      <div className="amount mt-4">
        <h5 className="text-center">Amount</h5>
        <div>
          <input type="number" />
          <button className="changeBtn">
            <i className="fa-solid fa-rotate"></i>
          </button>
        </div>

        <p className="text-center mt-3">netice</p>
      </div>

      <OtherCurrencies />
    </div>
  );
}

export default Exchange;
